import React from 'react';

export default class ItemCard extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            item: null,
            text: null,
            editActive: false
        }
    }
    handleItemClick() {
        this.setState({
            item: i,
            editActive: !this.state.editActive
        });
    }
    handleItemKeyPress = (event) => {
        if (event.code === "Enter") {
            this.handleBlur();
        }
    }
    handleBlur() {
        let textValue = input;
        this.props.renameItemCallback(this.state.i, textValue);
        this.handleItemClick();
    }
    render() {
        const {currentList, i} = this.props;
        if (this.state.editActive) {
            return (
                <input
                    id={"list-item-" + i}
                    className='list-'
                    type='text'
                    onKeyPress={this.handleItemKeyPress}
                    onBlur={this.handleBlur}
                    onChange={this.handleUpdate}
                    defaultValue={currentList.items[i]}
                />)
        }
        else {
            return (
                <div
                    onDoubleClick={this.handleItemClick.bind(this)}
                >
                    {currentList.items[i]}
                </div>
            );
        }
    }
}
