import React from 'react';

export default class ItemCard extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            item: null,
            text: null,
            editActive: false
        }
    }
    handleItemClick() {
        this.setState({
            editActive: !this.state.editActive
        });
    }
    handleItemKeyPress = (event) => {
        if (event.code === "Enter") {
            this.handleBlur();
        }
    }
    handleUpdate = (event) => {
        this.setState(
            { text: event.target.value });
    }
    handleBlur() {
        console.log(this.props.i);
        let textValue = this.state.text;
       // this.setState(prevState => {
         //   item: id
      //  })
        this.props.renameItemCallback(this.id, textValue);
        this.handleItemClick();
    }
    render() {
        const {currentList, i} = this.props;
        if (this.state.editActive) {
            return (
                <input
                    id={i}
                    className='list-'
                    type='text'
                    onKeyPress={this.handleItemKeyPress}
                    onBlur={this.handleBlur(id)}
                    onChange={this.handleUpdate}
                    defaultValue={currentList.items[i]}
                />)
        }
        else {
            return (
                <div
                    onDoubleClick={this.handleItemClick.bind(this)}
                >
                    {currentList.items[i]}
                </div>
            );
        }
    }
}
