import React from "react";
import ItemCard from "./ItemCard";
export default class Workspace extends React.Component {
    handleDragover = (event) => {
        event.preventDefault();
    }
    handleDrop = (event) => {
    }
    handleDragStart = (ev) => {
        ev.dataTransfer.setData("text", ev.target.id);
    }
    handleDragEnter = (event) => {
        event.currentTarget.style.background = "#669966";
    }
    handleDragLeave = (event) => {
        event.currentTarget.style.background = "#e1e4cb";
    }
    render() {
        const {currentList, renameItemCallback} = this.props;
        if (currentList === null) {
            return (
                <div id="top5-workspace">
                    <div id="workspace-edit">
                        <div id="edit-numbering">
                            <div className="item-number">1.</div>
                            <div className="item-number">2.</div>
                            <div className="item-number">3.</div>
                            <div className="item-number">4.</div>
                            <div className="item-number">5.</div>
                        </div>
                        <div id ="edit-items">
                            <div className="top5-item"></div>
                            <div className="top5-item"></div>
                            <div className="top5-item"></div>
                            <div className="top5-item"></div>
                            <div className="top5-item"></div>
                        </div>
                    </div>
                </div>
            )
        }
        else {
            return (
                <div id="top5-workspace">
                    <div id="workspace-edit">
                        <div id="edit-numbering">
                            <div className="item-number">1.</div>
                            <div className="item-number">2.</div>
                            <div className="item-number">3.</div>
                            <div className="item-number">4.</div>
                            <div className="item-number">5.</div>
                        </div>
                        <div id="edit-items">
                            <div className="top5-item">
                                {
                                <ItemCard
                                    i={0}
                                    currentList={currentList}
                                    renameItemCallback={renameItemCallback}
                                    onDragOverCallback={this.handleDragover}
                                    onDragStartCallback={this.handleDragStart}
                    onDropCallback={this.handleDrop}
                    onDragEnterCallback={this.handleDragEnter}
                    onDragLeaveCallback={this.handleDragLeave}
                                    />
                                }
                            </div>
                            <div className="top5-item">{
                                <ItemCard
                                    i={1}
                                    currentList={currentList}
                                    renameItemCallback={renameItemCallback}
                                    />
                                }</div>
                            <div className="top5-item">{
                                <ItemCard
                                    i={2}
                                    currentList={currentList}
                                    renameItemCallback={renameItemCallback}
                                    />
                                }</div>
                            <div className="top5-item">{
                                <ItemCard
                                    i={3}
                                    currentList={currentList}
                                    renameItemCallback={renameItemCallback}
                                    />
                                }</div>
                            <div className="top5-item">{
                                <ItemCard
                                    i={4}
                                    currentList={currentList}
                                    renameItemCallback={renameItemCallback}
                                    />
                                }</div>
                        </div>
                    </div>
                </div>
            )
        }
    }
}
